//same div height by element height
function equalheight(input, dwidth)
{
$(window).bind("load", function() {
	var maxheight = 0;
	var maxwidth = 0;
	var count = 0;

	if (dwidth) {
		$('' + input + '').each(function() {
			if ($(this).width() > maxwidth) {
				maxwidth = $(this).width();
			}
		});

		$('' + input + '').css({'width': maxwidth});
	} else {

		$('' + input + '').each(function() {
			if ($(this).height() > maxheight) {
				maxheight = $(this).height();
			}
		});

		$('' + input + '').css({'height': maxheight});
	}
});
}

